import React from "react";
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";
// @material-ui/icons
// core components
import GridContainer from "components/Grid/GridContainer.js";
import GridItem from "components/Grid/GridItem.js";
import image from "assets/img/bg7.jpg";
import Card from "components/Card/Card.js";
import CardBody from "components/Card/CardBody.js";
import CardHeader from "components/Card/CardHeader.js";
import styles from "assets/jss/material-kit-react/views/loginPage.js";
import TextField from '@material-ui/core/TextField';
import Header from "components/Header/Header.js";
import { encrypt } from "utils/cryto";
import { decrypt } from "utils/cryto";
import { Box } from "@material-ui/core";
const useStyles = makeStyles(styles);

export default function RootPage() {
    const classes = useStyles();
    const [halfWidth, setHalfWidth] = React.useState('');
    const [rounds, setRounds] = React.useState('');
    const handleChange = (e) => {
        if (e.target.id === "round") {
            setRounds(e.target.value);
        }
        else if (e.target.id === "halfWidth") {
            setHalfWidth(e.target.value);
        }
    }
    return (
        <div>
            <div className={classes.container}>
                <div className={classes.pageHeader}>
                    <Header
                        brand="Demonstration of Caesar Cipher"
                        color="dark"
                        fixed
                    />
                </div>
                <div className={classes.container}>
                    <form>
                        <TextField
                            autoFocus
                            id="rounds"
                            value={rounds}
                            onChange={handleChange}
                            variant="outlined"
                            label="No of Rounds"
                            helperText={
                                "This feild can't be left Empty."
                            }
                        />
                        <TextField
                            autoFocus
                            id="halfWidth"
                            value={halfWidth}
                            onChange={handleChange}
                            variant="outlined"
                            label="Half Width"
                            helperText={
                                "Half Width of the data block"
                            }
                        />
                        <Button color="dark"></Button>
                    </form>
                </div>
            </div>

        </div>
    );
}
